// Get input from previous node (usually an HTTP Request)
const input = items[0].json;

// Ensure we always have an array to loop through
const data = Array.isArray(input) ? input : [input];

const providerSet = new Set();

// Loop through each movie entry
for (const movie of data) {
  const results = movie.results || {};

  for (const region in results) {
    const regionData = results[region];

    // Check all possible provider types
    const providerTypes = ["ads", "flatrate", "rent", "buy"];
    for (const type of providerTypes) {
      if (Array.isArray(regionData[type])) {
        regionData[type].forEach(provider => {
          if (provider.provider_name) {
            providerSet.add(provider.provider_name.trim());
          }
        });
      }
    }
  }
}

// Join all provider names with commas
const providerList = Array.from(providerSet).join(", ");

// Return as a single n8n item
return [
  {
    json: {
      provider_name: providerList
    }
  }
];

